        this._x += p._x
        this._y += p._y
        this._z += p._z
        this._t += p._t
        return this
    }

    subtractInPlace (p) {
        this._x -= p._x
        this._y -= p._y
        this._z -= p._z
        this._t -= p._t
        return this
    }

