        }

        /*
        if (this._t) { 
            s += ", " + this._t + "t" 
        }
        */

        return s + ")"
    }

    distanceFromOrigin () {
        const ds = Math.pow(this.x(), 2) + Math.pow(this.y(), 2) + Math.pow(this.z(), 2)
        return Math.sqrt(ds)
    }

    // difference with another point

    dxFrom (p) {
        return this.x() - p.x()
    }

    dyFrom (p) {
        return this.y() - p.y()
    }

    dzFrom (p) {
        return this.z() - p.z()
    }

    dtFrom (p) {
        return this.t() - p.t()
    }

    distanceFrom (p) {
        const dx = this.dxFrom(p)
        const dy = this.dyFrom(p)
        const dz = this.dzFrom(p)
        return Math.sqrt(dx*dx + dy*dy + dz*dz)
    }

