        return (this.x() === p.x()) && (this.y() === p.y()) && (this.z() === p.z()) && (this.t() === p.t())
    }

    // comparison 

    isGreaterThan (p) {
        return this.x() > p.x() && this.y() > p.y()
    }

    isLessThan (p) {
        return this.x() < p.x() && this.y() < p.y()
    }

    isGreaterThanOrEqualTo (p) {
        return this.x() >= p.x() && this.y() >= p.y()
    }

    isLessThanOrEqualTo (p) {
        return this.x() <= p.x() && this.y() <= p.y()
    }

