    }

    angleInDegrees () {
        return this.angleInRadians() * 180 / Math.PI;
    }

    angleInRadiansTo (p) {
        return p.subtract(this).angleInRadians()
    }

    angleInDegreesTo (p) {
        return p.subtract(this).angleInDegrees()
    }

    midpointTo (p) {
