    }

    multiplyByScalar (v) {
        const p = Point.clone()
        p.set(this.x() * v, this.y() * v, this.z() * v)
        return p
    }

    divideByScalar (v) {
        return this.multiplyByScalar(1/v)
    }

    negated (p) {
        return this.multiplyByScalar(-1)
    }

    // css

    asCssStringWithUnitSuffix (name, unitSuffix) {
        if (!unitSuffix) { 
