        return this.asCssStringWithUnitSuffix("rotate3d", "deg")
    }

    asCssScale3dString () {
        return this.asCssStringWithUnitSuffix("scale3d", "")
    }

    // size - TODO: move to Size type?

    width () {
        return this.x()
    }

    height () {
        return this.y()
    }

}.initThisClass());

