    init () {
        super.init()
        this.setListenerClasses(this.defaultListenerClasses()) 
        this.setMinDistToBegin(5)
        this.setIsDebugging(false)
        return this
    }

    start () {
        this.startDocListeners() // only want to listen to the document
        return this
    }

