        const tr = this.timeRatioDone()
        const newValue = Math.ceil((this.timeRatioDone() * (this.currentValue() - this.startValue())) + this.startValue());
        this.setValue(newValue)

        if (tr !== 1) {
            requestAnimationFrame(() => { this.nextFrame() })
