        this.setJustifyContent("center")
        return this
    }

    canSplit () {
        return this.subviews().length === 0
    }

    addSubviewCount (count) {
        for (let i = 0; i < count; i++) {
            this.newFlexSubview()     
        }
        return this
    }

    newFlexSubview () {
        const v = FlexDomView.clone()
        v.setDisplay("flex")
        v.setMinHeight("0em")
