    onUpArrowKeyDown (event) { // why down and not up?
        if (!this.canNavigate()) { 
            return 
        }

        if (this.isVertical()) {
            this.moveDown()
        } else {
            this.moveLeft()
        }
        return false
    }

    onDownArrowKeyDown (event) { // why down and not up?
        if (!this.canNavigate()) { 
            return 
        }

        if (this.isVertical()) {
            this.moveUp()
        } else {
            this.moveRight()
        }
        return false
    }

    onLeftArrowKeyUp (event) {
        if (!this.canNavigate()) { 
            return this
        }	
        if (this.isVertical()) {
            this.moveLeft()
        } else {
            this.moveDown()
        }
    }

    onRightArrowKeyUp (event) {
        if (!this.canNavigate()) { 
            return this
        }	

        if (this.isVertical()) {
            this.moveRight()
        } else {
            this.moveUp()
        }
    }

    // --- arrow moves ---

    moveLeft () {
        const pc = this.previousItemSet()	
        if (pc) {
            if (this.selectedTile()) { 
                this.selectedTile().unselect() 
            }

            const newSelectedTile = pc.selectedTile()
            newSelectedTile.setShouldShowFlash(true).updateSubviews()
            pc.didTapItem(newSelectedTile)
        	this.selectPreviousColumn()

            //debugger;
            pc.didChangeNavSelection()
        }
        return this
    }

    moveRight () {
        this.selectNextColumn()
        return this
    }

    moveUp () {
        this.selectNextTile()
        this.showSelected()
        return this
    }

    moveDown () {
        this.selectPreviousTile()
        this.showSelected()
        return this
    }

    // --- escape ---

    onEscapeKeyDown (event) {
        //this.setIsColumnInspecting(false)

        if (!this.canNavigate()) { 
            return this
        }	

        this.moveLeft()
        //return true
    }

    // --- enter key begins tile editing ---

    onEnterKeyUp (event) {        
        if (!this.canNavigate()) { 
            return this
        }

        // carefull - if Tile is registered fro keyboard,
        // this may cause onEnterKeyUp to be sent twice
        const tile = this.selectedTile()
        if (tile) { 
		    tile.onEnterKeyUp(event)
        }

        return false
    }

    // --- keyboard controls, add and delete actions ---

    /*
    deleteTile (aTile) {
        let sNode = aTile.node()
        if (sNode && sNode.canDelete()) { 
			sNode.performAction("delete") 
		}
        return this
    }

    deleteSelectedTiles () {
        this.selectedTiles().forEach(r => this.deleteTile(r))

        if (this.tiles().length === 0) {
            this.selectPreviousColumn()
        }
    }
    */

    onShiftDeleteKeyUp (event) {
        if (!this.canNavigate()) { 
            return 
        }

        //this.deleteSelectedTiles()
        return false
    }

    onPlusKeyUp (event) {
        if (!this.canNavigate()) { 
            return 
        }		

        const sNode = this.selectedNode()
        if (sNode && sNode.hasAction("add")) { 
            const newNode = sNode.performAction("add") 
            this.selectNextColumn()
            if (this.nextColumn()) {
                this.nextColumn().selectTileWithNode(newNode)
            }
        }
        return false		
    }


}.initThisCategory());

