                file.setResourceSize(entry.size)
                if (!file) {
                    throw new Error("no file added")
                }
            }) // will find path to last folder and insert resource
            this.setHasSetupSubnodes(true)
        }
        return this
    }

    rootFolder () {
        this.setupSubnodesIfNeeded()
        //debugger
        return this.subnodes().first()
    }


}.initThisClass());

