  setupAsDefault() {
    this.setTitle("DefaultThemeClass");
    this.setupSubnodes();
    this.subnodes().forEach((sn) => sn.didInit());

    this.setupActiveDefault();
    this.setupUnselectedDefault();
    this.setupSelectedDefault();
    this.setupDisabledDefault();

